    private void btnaddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnaddActionPerformed
        String nome = tfNome.getText();
        String marca = tfMarca.getText();
        String modelo = tfModelo.getText();
        String ano = tfAno.getText();
        String motorizacao = tfMotorizacao.getText();
        if (nome.isEmpty() || marca.isEmpty() || modelo.isEmpty() || ano.isEmpty() || motorizacao.isEmpty()) {
            JOptionPane.showMessageDialog(this,
                    "por favor insira todos os planos",
                    "tente novamente",
                    JOptionPane.ERROR_MESSAGE);
        } else {
            DefaultTableModel model = (DefaultTableModel) TabelaCarro.getModel();
            model.addRow(new Object[]{nome, marca, modelo, ano, motorizacao});
            tfNome.setText("");
            tfMarca.setText("");
            tfModelo.setText("");
            tfAno.setText("");
            tfMotorizacao.setText("");
            
        }
    }//GEN-LAST:event_btnaddActionPerformed

