    private void btndelActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btndelActionPerformed
            int row = TabelaCarro.getSelectedRow();
            
            if (row < 0) { JOptionPane.showMessageDialog(this, "Nenhuma fileira selecionada", "Selecione uma fileira", JOptionPane.ERROR_MESSAGE);
           
            } else {
                DefaultTableModel model = (DefaultTableModel) TabelaCarro.getModel();
                model.removeRow(row);
            }
    }//GEN-LAST:event_btndelActionPerformed

    private void formWindowClosing(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowClosing
                DefaultTableModel model = (DefaultTableModel) TabelaCarro.getModel();
                Vector<Vector> tableData = model.getDataVector();
                
                try {
                    FileOutputStream file = new FileOutputStream("file.bin");
                    ObjectOutputStream output = new ObjectOutputStream(file);
                    output.writeObject(tableData);
                    output.close();
                    file.close();
                    
                } catch (Exception ex) {
                    ex.printStackTrace();
                }    
    }//GEN-LAST:event_formWindowClosing

